// Updates Page - Presentation Layer

import React, { useState } from 'react';
import PageLayout from '../../components/layout/PageLayout';
import { Card, CardHeader, CardTitle, CardBody } from '../../components/ui/Card';
import Button from '../../components/ui/Button';
import { useData } from '../../hooks/useData';

const UpdatesPage = () => {
  const { items, loading, addItem } = useData('updates');
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    title: '',
    content: '',
    category: '',
    priority: 'normal',
    author: ''
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await addItem(formData);
      setFormData({
        title: '',
        content: '',
        category: '',
        priority: 'normal',
        author: ''
      });
      setShowForm(false);
    } catch (error) {
      console.error('Error adding update:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const getPriorityColor = (priority) => {
    const colors = {
      low: 'bg-blue-100 text-blue-800',
      normal: 'bg-green-100 text-green-800',
      high: 'bg-yellow-100 text-yellow-800',
      urgent: 'bg-red-100 text-red-800'
    };
    return colors[priority] || colors.normal;
  };

  return (
    <PageLayout 
      title="Community Updates" 
      subtitle="Stay informed with the latest community news and updates"
    >
      <div className="fade-in">
        {/* Add Update Button */}
        <div className="mb-6">
          <Button 
            onClick={() => setShowForm(!showForm)}
            className="mb-4"
          >
            {showForm ? 'Cancel' : 'Post Update'}
          </Button>
        </div>

        {/* Add Update Form */}
        {showForm && (
          <Card className="mb-8">
            <CardHeader>
              <CardTitle>Post Community Update</CardTitle>
            </CardHeader>
            <CardBody>
              <form onSubmit={handleSubmit}>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div className="form-group">
                    <label className="form-label">Title</label>
                    <input
                      type="text"
                      name="title"
                      value={formData.title}
                      onChange={handleInputChange}
                      className="form-input"
                      placeholder="Update title"
                      required
                    />
                  </div>

                  <div className="form-group">
                    <label className="form-label">Category</label>
                    <select
                      name="category"
                      value={formData.category}
                      onChange={handleInputChange}
                      className="form-input"
                      required
                    >
                      <option value="">Select category</option>
                      <option value="general">General</option>
                      <option value="events">Events</option>
                      <option value="safety">Safety</option>
                      <option value="maintenance">Maintenance</option>
                      <option value="announcements">Announcements</option>
                    </select>
                  </div>

                  <div className="form-group">
                    <label className="form-label">Priority</label>
                    <select
                      name="priority"
                      value={formData.priority}
                      onChange={handleInputChange}
                      className="form-input"
                    >
                      <option value="low">Low</option>
                      <option value="normal">Normal</option>
                      <option value="high">High</option>
                      <option value="urgent">Urgent</option>
                    </select>
                  </div>

                  <div className="form-group">
                    <label className="form-label">Author</label>
                    <input
                      type="text"
                      name="author"
                      value={formData.author}
                      onChange={handleInputChange}
                      className="form-input"
                      placeholder="Your name"
                      required
                    />
                  </div>
                </div>

                <div className="form-group">
                  <label className="form-label">Content</label>
                  <textarea
                    name="content"
                    value={formData.content}
                    onChange={handleInputChange}
                    className="form-input form-textarea"
                    placeholder="Update content"
                    required
                  />
                </div>

                <div className="flex gap-4">
                  <Button type="submit">Post Update</Button>
                  <Button 
                    type="button" 
                    variant="secondary"
                    onClick={() => setShowForm(false)}
                  >
                    Cancel
                  </Button>
                </div>
              </form>
            </CardBody>
          </Card>
        )}

        {/* Updates List */}
        <div>
          <h3 className="text-2xl font-semibold mb-6">Recent Updates</h3>

          {loading ? (
            <div className="text-center py-8">
              <div className="spinner mx-auto mb-4"></div>
              <p>Loading updates...</p>
            </div>
          ) : items.length === 0 ? (
            <Card>
              <CardBody className="text-center py-8">
                <p className="text-grey-600">No updates posted yet.</p>
                <p className="text-sm text-grey-500 mt-2">
                  Be the first to share community news!
                </p>
              </CardBody>
            </Card>
          ) : (
            <div className="space-y-6">
              {items.map((item) => (
                <Card key={item.id}>
                  <CardHeader>
                    <div className="flex items-center justify-between">
                      <CardTitle className="text-xl">{item.title}</CardTitle>
                      <span className={`px-2 py-1 rounded text-xs font-medium ${getPriorityColor(item.priority)}`}>
                        {item.priority}
                      </span>
                    </div>
                    <div className="flex items-center gap-4 text-sm text-grey-500">
                      <span>By: {item.author}</span>
                      <span>Category: {item.category}</span>
                      <span>{new Date(item.date).toLocaleDateString()}</span>
                    </div>
                  </CardHeader>
                  <CardBody>
                    <p className="text-grey-700 whitespace-pre-wrap">{item.content}</p>
                  </CardBody>
                </Card>
              ))}
            </div>
          )}
        </div>
      </div>
    </PageLayout>
  );
};

export default UpdatesPage;
