// Feature Grid component - Presentation Layer

import React from 'react';
import { useNavigation } from '../../hooks/useNavigation';

const FeatureGrid = () => {
  const { navigationItems, navigateTo } = useNavigation();

  // Filter out the home item for the feature grid
  const featureItems = navigationItems.filter(item => item.path !== '/');

  return (
    <div className="features-grid">
      {featureItems.map((item) => (
        <div
          key={item.path}
          className="feature-card"
          onClick={() => navigateTo(item.path)}
          role="button"
          tabIndex={0}
          onKeyDown={(e) => {
            if (e.key === 'Enter' || e.key === ' ') {
              e.preventDefault();
              navigateTo(item.path);
            }
          }}
        >
          <div className="feature-icon text-6xl mb-4">
            {item.icon}
          </div>
          <h3 className="feature-title">
            {item.label}
          </h3>
          <p className="feature-description">
            {getFeatureDescription(item.path)}
          </p>
        </div>
      ))}
    </div>
  );
};

// Helper function to get feature descriptions
const getFeatureDescription = (path) => {
  const descriptions = {
    '/lost-and-found': 'Find lost items or report found items in your community',
    '/carpool': 'Share rides and find carpool partners for your daily commute',
    '/updates': 'Stay informed with the latest community news and updates',
    '/education': 'Discover local classes, workshops, and educational opportunities',
    '/business': 'Explore local businesses and services in your area',
    '/phone-book': 'Access community contact information and emergency numbers',
    '/sell': 'Buy and sell items within your local community'
  };

  return descriptions[path] || 'Explore this feature to learn more';
};

export default FeatureGrid;
