// Home Page - Presentation Layer

import React from 'react';
import PageLayout from '../components/layout/PageLayout';
import WeatherCard from '../components/layout/WeatherCard';
import NewsFeed from '../components/layout/NewsFeed';
import FeatureGrid from '../components/layout/FeatureGrid';

const HomePage = () => {
  return (
    <PageLayout>
      <div className="fade-in">
        {/* Weather Section */}
        <div className="mb-8">
          <WeatherCard />
        </div>

        {/* Features Section */}
        <div>
          <h2 className="text-3xl font-semibold text-center mb-8 text-grey-dark">
            Community Features
          </h2>
          <FeatureGrid />
        </div>

        {/* Welcome Message */}
        <div className="mt-12 text-center">
          <div className="bg-white rounded-lg p-8 shadow-md">
            <h3 className="text-2xl font-semibold mb-4 text-grey-dark">
              Welcome to LocalHub
            </h3>
            <p className="text-grey-600 max-w-2xl mx-auto">
              Your local community hub connecting neighbors, sharing resources, 
              and building stronger communities. Explore our features to discover 
              what's happening in your area.
            </p>
          </div>
        </div>
      </div>
    </PageLayout>
  );
};

export default HomePage;
